public class Assign8 {

	/**
	 * 1)display Alphabets reverse order using do while.
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='z';
		do {
			System.out.print(ch);
			ch--;
		}while(ch>='a');

	}

}
